package com.example.samipc.mysidurapp1.HebrewDate;import java.util.*;/*** Concrete subclass of SunRiseSet.<P>* This uses the classes written by <A HREF="http://www.kevinboone.com/">Kevin Boone</A>.*/public class DefaultSunRiseSet extends SunRiseSet{	private SunTimes sunTimes= new SunTimes();	public Calendar getSunriseTime()	{		try		{			Time time= sunTimes.getSunriseTimeUTC(date.get(date.YEAR), date.get(date.MONTH)+ 1, date.get(date.DAY_OF_MONTH), longitude, latitude, SunTimes.CIVIL_ZENITH);			return getDateFromTime(time);		}		catch (SunTimesException e)		{			e.printStackTrace();		}		return null;	}	private Calendar getDateFromTime(Time time)	{		Calendar cal= (Calendar) date.clone();		cal.set(cal.HOUR_OF_DAY, time.getHour());		cal.set(cal.MINUTE, time.getMinute());		cal.set(cal.SECOND, time.getSecond());		return cal;	}	public Calendar getSunsetTime()	{		try		{			Time time= sunTimes.getSunsetTimeUTC(date.get(date.YEAR), date.get(date.MONTH)+ 1, date.get(date.DAY_OF_MONTH), longitude, latitude, SunTimes.ZENITH);			return getDateFromTime(time);		}		catch (SunTimesException e)		{			e.printStackTrace();		}		return null;	}}